export const initialState = {
  mainPosts: [
    {
      id: 1,
      User: {
        id: 1,
        nickname: "hyoseok",
      },
      content: "첫 번째 게시글 #해시태크, #첫번째 글",
      Images: [{}, {}, {}],
      Comments: [
        {
          User: {
            nickname: "babo",
          },
          content: "두번째 게시글",
        },
        {
          User: {
            nickname: "babo2",
          },
          content: "두번째 eotrmf",
        },
      ],
      imagePaths: [],
      postAdded: false,
    },
  ],
};

const ADD_POST = "ADD_POST";
export const addPost = {
  type: ADD_POST,
};
const dummyPost = {
  id: 2,
  content: "더미데이터",
  User: {
    id: 1,
    nickname: "hyoseok",
  },
  Images: [],
  Comments: [],
};

const reducer = (state = initialState, action) => {
  switch (action.type) {
    case ADD_POST:
      return {
        ...state,
        mainPosts: [dummyPost, ...state.mainPosts],
        postAdded: true,
      };
    default:
      return state;
  }
};

export default reducer;
