import React from "react";
import {
  Card,
  RetweetOutlined,
  HeartOutlined,
  MessageOutlined,
  Popover,
  EllipsisOutlined,
  Button,
} from "antd";
const PostCard = () => {
  return (
    <div>
      <Card
        cover={
          postMessage.Images[0] && <PostImages images={postMessage.Images} />
        }
        actions={[
          <RetweetOutlined key="retweet" />,
          <HeartOutlined key="hear" />,
          <MessageOutlined key="comment" />,
          <Popover
            key="more"
            content={
              <Button.Group>
                <Button>수정</Button>
                <Button type="danger">삭제</Button>
                <Button>신고</Button>
              </Button.Group>
            }
          >
            <EllipsisOutlined />
          </Popover>,
        ]}
      >
        <Image />
        <Content />
        <Buttons></Buttons>
      </Card>
      <CommentForm />
      <Comments />
    </div>
  );
};

export default PostCard;
